sci:arg.sci:value:
contract(',',RGroupList);

IF (SourceTag is defined) THEN
  RGroupList := 'core';
END IF;

#displayName := trim(Parameter('Expression', 2));
if(strlength(#displayName) > 0) then	
	/* Find the first line break */
	#nl := FindSubstring(#displayName, "\r");
	if #nl <= 0 then
		#nl := FindSubstring(#displayName, "\n");
	end if;

	/* Keep the first line only */
	if (#nl > 0) then
		StrErase(#displayName, #nl);
	end if;
	
	#displayName;
else
	"";
end if;

if (strlength(trim(parameter('MergeUsing', 2))) > 0) then
	"Merge on " . trim(parameter('MergeUsing', 2));
else
	"";
end if;

#displayName := trim(Parameter('Expression', 2));
if(strlength(#displayName) > 0) then	
	/* Find the first line break */
	#nl := FindSubstring(#displayName, "\r");
	if #nl <= 0 then
		#nl := FindSubstring(#displayName, "\n");
	end if;

	/* Keep the first line only */
	if (#nl > 0) then
		StrErase(#displayName, #nl);
	end if;
	
	#displayName;
else
	"";
end if;

/* Start from 'Property 1' since it must be defined.
   Keep this following line for the client auto-rename to use.
	 Parameter('Property 1'), 
     Parameter('Property 2'), 
	 Parameter('Property 3')
	 Parameter('Property 4')
	 Parameter('Property 5')
 */
if (trim(strlength(parameter('Property 1', 2))) > 0) then
	#name := "Sort on " . trim(parameter('Property 1', 2));
	
	/* Get other sort properties that have values */
	#paramKey := 'Property ';
	#count := 2;
	#paramName := #paramKey . #count;

	while (#count <= 5 and parameter(#paramName, 2) is defined)
	loop
		if (strlength(parameter(#paramName, 2)) > 0) then		
			#name := #name . ', ';
			#name := #name . parameter(#paramName, 2);
		end if;
	
		#count++;
		#paramName := #paramKey . #count;
	end loop;
	#name;
else
	"";
end if;

sci:arg.sci:legalvalscript:
if @PPDepictionSettingsXML is not defined then
          @PPDepictionSettingsXML := RunProtocol(
          'Components/Chemistry/Utilities/Internals/Reference Components/List Global Depiction Settings (Dynamic Parameter)',
          'PPDepictionSettingsXML');
          end if;

          SetLegalValues(@PPDepictionSettingsXML);

sci:arg.sci:validscript:
#n := 0;
          #s := parameter();
          #len := stringlen(#s);
          for #i in 1 .. #len loop
          if substring(#s, #i, 1) eq ',' then
          #n++;
          end if;
          end loop;

          SetInvalidDetails('Must define four labels separated by commas');

          parameter('Chirality Label Options') eq 'Custom Labels' and (#n eq 3)

sci:arg.sci:enabledscript:
SetDisabledDetails(
	      'To enable '.ParameterName().', set Sheets to "SheetName"');
(parameter('Sheets') eq 'SheetName')

